
jQuery(document).ready(function($){var desfaseItem='.carousel';var desfaseItem='.destacado-estilo';var desfaseDerecho='.thumb';var apareceItem='.entry-title';var textoLoco='#carrusel-homeslide .carousel-caption h1';$(desfaseItem).carousel({interval:false});$(window).bind("load resize scroll",function(e){var y=$(window).scrollTop();$(desfaseItem).filter(function(){return $(this).offset().top<(y+$(window).height())&&$(this).offset().top+$(this).height()>y;}).css('background-position','center '+parseInt(y/ -2)+'px');$(desfaseDerecho).filter(function(){return $(this).offset().top<(y+$(window).height())&&$(this).offset().top+$(this).height()>y;}).css('background-position','right '+parseInt(y/2)+'px');});$(window).scroll(function(){$(apareceItem).each(function(i){var bottom_of_object=$(this).offset().top+$(this).outerHeight();var bottom_of_window=$(window).scrollTop()+$(window).height();if(bottom_of_window>bottom_of_object){$(this).animate({'opacity':'1'},500);}});});var size=['16px','24px','32px','40px','44px'];var weight=['100','300','700','900'];$(textoLoco).each(function(){$(this).html($(this).text().split(' ').map(function(v){return'<span style="font-size:'+size[Math.floor(Math.random()*size.length)]+';font-weight:'+weight[Math.floor(Math.random()*weight.length)]+'">'+v+'</span>';}).join(' '));});$('.carousel').carousel({swipe:30});});